    while ((bytes_read = fread(buffer, 1, sizeof(buffer), src)) > 0) {
        fwrite(buffer, 1, bytes_read, dest);
    }
    fclose(src);
    fclose(dest);
    const char *response = "COPY: Success\n";
    send(nm_sock, response, strlen(response), 0);
