    ao_sample_format format;
    memset(&format, 0, sizeof(format));
    format.bits = 16;
    format.channels = 2;
    format.rate = 44100;
    format.byte_format = AO_FMT_NATIVE;
    ao_device *device = ao_open_live(driver, &format, NULL);

    if (device == NULL) {
        fprintf(stderr, "Error opening device.\n");
        return;
    }

    // Initialize mpg123
    mpg123_init();
    mpg123_handle *mh = mpg123_new(NULL, NULL);
    unsigned char buffer[BUFFER_SIZE];
    size_t done;
    int err;

    if (mpg123_open_feed(mh) != MPG123_OK) {
        fprintf(stderr, "Unable to open mpg123 feed.\n");
        return;
    }

    int bytes_received;
    while ((bytes_received = recv(sock, buffer, sizeof(buffer), 0)) > 0) {
        if (mpg123_feed(mh, buffer, bytes_received) != MPG123_OK) {
            fprintf(stderr, "Error feeding data to mpg123.\n");
            break;
        }

        while (mpg123_read(mh, buffer, sizeof(buffer), &done) == MPG123_OK) {
            ao_play(device, (char *)buffer, done);
        }
    }

    // Cleanup
    mpg123_close(mh);
    mpg123_delete(mh);
    mpg123_exit();
