        printf("Client Port: %d\n", ss_info.port_client);
        printf("Accessible Paths: %s\n", ss_info.accessible_paths);

        // Tokenize accessible paths using comma as delimiter
        char *token = strtok(ss_info.accessible_paths, ",");
