        handle_copy(new_socket, arg1, arg2);
    } else {
        const char *response = "ERROR: Invalid command from Naming Server\n";
        send(new_socket, response, strlen(response), 0);
    }

    close(new_socket);
    free(thread_args);
    return NULL;
